import { useState } from 'react';
import axios from 'axios';
import { useDisclosure } from '@chakra-ui/react';

type Loading = boolean;

type GitHubData = {
  id: number;
  full_name: string;
  owner: [];
  language: string;
  stargazers_count: number;
  watchers_count: number;
  forks_count: number;
  open_issues_count: number;
};

type Number = number;
export const Method = () => {
  const [inputValue, setInputValue] = useState<String | undefined>('');
  const [loading, setLoading] = useState<Loading>(false);
  const [gitHubData, setGitHubData] = useState<GitHubData[] | undefined>(
    undefined
  );
  const [repoData, setRepoData] = useState<GitHubData[] | undefined>(undefined);
  const [language, setLanguage] = useState<String | undefined>(undefined);
  const [starts, setStarts] = useState<Number | undefined>(undefined);
  const [watch, setWatch] = useState<Number | undefined>(undefined);
  const [forks, setForks] = useState<Number | undefined>(undefined);
  const [issues, setIssues] = useState<Number | undefined>(undefined);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const textChange = (e: any) => {
    setInputValue(e.target.value);
  };

  const downEnter = (e: any) => {
    if (inputValue === '') {
      return;
    }
    if (e.key === 'Enter') {
      setLoading(true);
      axios
        .get(
          `https://api.github.com/search/repositories?q=${inputValue}+in:name&sort=stars`
        )
        .then((res) => {
          setGitHubData(res.data.items);
          setLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setLoading(false);
        });
    }
  };
  const handleRequest = () => {
    setLoading(true);
    axios
      .get(
        `https://api.github.com/search/repositories?q=${inputValue}+in:name&sort=stars`
        // 'https://developer.github.com/v3/orgs/#get-an-organization'
      )
      .then((res) => {
        console.log('rex', res);
        setGitHubData(res.data.items);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  };
  const handleData = (data: GitHubData) => {
    const updataGitHunData: GitHubData[] = [data];
    setRepoData(updataGitHunData);
    onOpen();
  };

  // const clickButtton = (e: React.FormEventHandler<HTMLFormElement>) => {
  //   console.log('aaaa', e);
  // };
  const clickButtton = () => {
    if (language) {
      const updataLanguage = gitHubData?.filter(
        (data) => data.language === language
      );
      setGitHubData(updataLanguage);
    }
    if (starts) {
      const updatastars = gitHubData?.filter(
        (data) => data.stargazers_count === starts
      );
      setGitHubData(updatastars);
    }
    if (watch) {
      const updatawatch = gitHubData?.filter(
        (data) => data.watchers_count === watch
      );
      setGitHubData(updatawatch);
    }
    if (forks) {
      const updataforks = gitHubData?.filter(
        (data) => data.forks_count === forks
      );
      setGitHubData(updataforks);
    }
    if (issues) {
      const updataIssue = gitHubData?.filter(
        (data) => data.open_issues_count === issues
      );
      setGitHubData(updataIssue);
    }
    console.log('git', gitHubData);
  };
  const filterLanguageTextChange = (e: any) => {
    setLanguage(e.target.value);
  };

  const filterStartTextChange = (e: any) => {
    setStarts(e.target.value);
  };

  const filterWatchTextChange = (e: any) => {
    setWatch(e.target.value);
  };

  const filterForksTextChange = (e: any) => {
    setForks(e.target.value);
  };

  const filterIssueTextChange = (e: any) => {
    setIssues(e.target.value);
  };

  return {
    loading,
    gitHubData,
    textChange,
    downEnter,
    inputValue,
    handleRequest,
    handleData,
    repoData,
    isOpen,
    onClose,
    clickButtton,
    filterLanguageTextChange,
    filterStartTextChange,
    filterWatchTextChange,
    filterForksTextChange,
    filterIssueTextChange,
  };
};
